class Veo3PromptGenerator {
  constructor() {
    this.initializeApp();
    this.bindEvents();
    this.populateDropdowns();
    this.populateBestPractices();
    this.populateTemplates();
  }

  initializeApp() {
    // Set default active tab to Basic Settings
    this.showTab('basic');
  }

  bindEvents() {
    // Tab navigation
    document.querySelectorAll('.tab-btn').forEach(btn => {
      btn.addEventListener('click', (e) => {
        e.preventDefault();
        const tabId = e.target.getAttribute('data-tab');
        console.log('Tab clicked:', tabId); // Debug log
        this.showTab(tabId);
      });
    });

    // Form change events for real-time prompt generation
    document.addEventListener('change', (e) => {
      if (e.target.matches('select, input, textarea')) {
        this.generatePrompt();
      }
    });

    document.addEventListener('input', (e) => {
      if (e.target.matches('input[type="text"], textarea')) {
        this.generatePrompt();
      }
    });

    // Hook type change for template preview
    document.getElementById('hook-type').addEventListener('change', (e) => {
      this.updateHookTemplate(e.target.value);
    });

    // Copy prompt button
    document.getElementById('copy-prompt').addEventListener('click', () => {
      this.copyPrompt();
    });

    // Random inspiration button
    document.getElementById('generate-random').addEventListener('click', () => {
      this.generateRandomSettings();
    });

    // Template selector
    document.getElementById('template-selector').addEventListener('change', (e) => {
      this.loadTemplate(e.target.value);
    });
  }

  showTab(tabId) {
    console.log('Showing tab:', tabId); // Debug log

    // Update tab buttons
    document.querySelectorAll('.tab-btn').forEach(btn => {
      btn.classList.remove('active');
    });
    const activeTabBtn = document.querySelector(`[data-tab="${tabId}"]`);
    if (activeTabBtn) {
      activeTabBtn.classList.add('active');
    }

    // Update tab content - hide all first
    document.querySelectorAll('.tab-content').forEach(content => {
      content.classList.remove('active');
    });

    // Show the selected tab content
    const activeTabContent = document.getElementById(`${tabId}-tab`);
    if (activeTabContent) {
      activeTabContent.classList.add('active');
      console.log('Activated tab content:', `${tabId}-tab`); // Debug log
    } else {
      console.error('Tab content not found:', `${tabId}-tab`); // Debug log
    }
  }

  populateDropdowns() {
    // Populate UGC types
    const ugcSelect = document.getElementById('ugc-type');
    if (ugcSelect) {
      appData.ugc_types.forEach(type => {
        const option = document.createElement('option');
        option.value = type.id;
        option.textContent = type.name;
        option.title = type.description;
        ugcSelect.appendChild(option);
      });
    }

    // Populate settings
    const settingSelect = document.getElementById('setting');
    if (settingSelect) {
