        option.value = setting.id;
        option.textContent = setting.name;
        option.title = setting.description;
        settingSelect.appendChild(option);
      });
    }

    // Populate lighting types
    const lightingSelect = document.getElementById('lighting');
    if (lightingSelect) {
      appData.lighting_types.forEach(lighting => {
        const option = document.createElement('option');
        option.value = lighting.id;
        option.textContent = lighting.name;
        option.title = lighting.description;
        lightingSelect.appendChild(option);
      });
    }

    // Populate actor types
    const actorSelect = document.getElementById('actor-type');
    if (actorSelect) {
      appData.actor_types.forEach(actor => {
        const option = document.createElement('option');
        option.value = actor.id;
        option.textContent = actor.name;
        option.title = actor.description;
        actorSelect.appendChild(option);
      });
    }

    // Populate hook types
    const hookSelect = document.getElementById('hook-type');
    if (hookSelect) {
      appData.hook_types.forEach(hook => {
        const option = document.createElement('option');
        option.value = hook.id;
        option.textContent = hook.name;
        option.title = hook.description;
        hookSelect.appendChild(option);
      });
    }

    // Populate camera movements
    const cameraSelect = document.getElementById('camera-movement');
    if (cameraSelect) {
      appData.camera_movements.forEach(movement => {
        const option = document.createElement('option');
        option.value = movement.id;
        option.textContent = movement.name;
        option.title = movement.description;
        cameraSelect.appendChild(option);
      });
    }
  }

  populateBestPractices() {
    const list = document.getElementById('best-practices-list');
    if (list) {
      appData.veo3_best_practices.forEach(practice => {
        const li = document.createElement('li');
        li.textContent = practice;
        list.appendChild(li);
      });
    }
  }

  populateTemplates() {
    const templateSelect = document.getElementById('template-selector');
    if (templateSelect) {
      appData.templates.forEach(template => {
        const option = document.createElement('option');
        option.value = template.id;
        option.textContent = template.name;
        templateSelect.appendChild(option);
      });
    }
  }

  updateHookTemplate(hookTypeId) {
    const hookType = appData.hook_types.find(h => h.id === hookTypeId);
    const templateText = document.getElementById('hook-template-text');

    if (templateText) {
      if (hookType) {
        templateText.textContent = hookType.template;
        templateText.style.fontStyle = 'italic';
        templateText.style.color = 'var(--color-text-secondary)';
      } else {
        templateText.textContent = 'Select a hook type to see the template';
        templateText.style.fontStyle = 'italic';
        templateText.style.color = 'var(--color-text-secondary)';
      }
    }
