      customHook: getData('custom-hook'),
      productPlacement: getData('product-placement'),
      productDemo: getData('product-demo'),
      brandVisibility: getData('brand-visibility'),
      cameraMovement: getData('camera-movement'),
      shotComposition: getData('shot-composition'),
      visualStyle: getData('visual-style'),
      colorGrading: getData('color-grading')
    };
  }

  generatePrompt() {
    const data = this.getFormData();
    const prompt = this.buildPrompt(data);

    // Update prompt display
    const promptOutput = document.getElementById('prompt-output');
    if (promptOutput) {
      if (prompt.trim()) {
        promptOutput.innerHTML = `<div class="generated-prompt">${this.formatPrompt(prompt)}</div>`;
      } else {
        promptOutput.innerHTML = '<p class="placeholder-text">Fill in the form to generate your Veo3 prompt...</p>';
      }
    }

    // Update quality score and word count
    this.updateQualityScore(data, prompt);
    this.updateWordCount(prompt);
  }

  buildPrompt(data) {
    if (!data.product) return '';

    let prompt = '';

    // Subject description
    if (data.ageRange || data.gender || data.actorType) {
      const age = data.ageRange ? data.ageRange.split('-')[0] + '-year-old' : '';
      const gender = data.gender || 'person';
      const actorDesc = this.getActorDescription(data.actorType);
      prompt += `A ${age} ${gender} ${actorDesc}`.replace(/\s+/g, ' ').trim();
    } else {
      prompt += 'A person';
    }

    // Setting and context
    if (data.setting) {
      const settingDesc = this.getSettingDescription(data.setting);
      prompt += ` in ${settingDesc}`;
    }

    // Product interaction
    if (data.productDemo) {
      const demoDesc = this.getProductDemoDescription(data.productDemo);
      prompt += `, ${demoDesc} ${data.product}`;
    } else {
      prompt += `, holding ${data.product}`;
    }

    prompt += '.';

    // Camera work
    if (data.cameraMovement || data.shotComposition) {
      const cameraDesc = this.getCameraDescription(data.cameraMovement, data.shotComposition);
      if (cameraDesc) {
        prompt += ` ${cameraDesc}`;
      }
    }

    // Lighting
    if (data.lighting) {
      const lightingDesc = this.getLightingDescription(data.lighting);
      if (lightingDesc) {
        prompt += ` ${lightingDesc}`;
      }
    }

    // Performance and dialogue
    if (data.performanceStyle) {
      const performanceDesc = this.getPerformanceDescription(data.performanceStyle);
      if (performanceDesc) {
        prompt += ` ${performanceDesc}`;
      }
    }

    // Hook or custom dialogue
    if (data.customHook) {
      prompt += ` "${data.customHook}"`;
    } else if (data.hookType) {
      const hookTemplate = this.getHookTemplate(data.hookType, data.product);
      if (hookTemplate) {
        prompt += ` "${hookTemplate}"`;
      }
    }

    // Technical specifications
    if (data.visualStyle) {
      prompt += ` Shot in ${data.visualStyle} style`;
      if (data.colorGrading) {
        prompt += ` with ${data.colorGrading.replace('-', ' ')} color grading`;
      }
      prompt += '.';
    }

    // Audio elements
    if (data.audioEnabled) {
      prompt += ' Audio includes clear speech';
      if (data.setting === 'outdoors') {
        prompt += ' with natural ambient sounds';
      } else if (data.ugcType === 'unboxing') {
        prompt += ', unboxing sounds, and paper rustling';
      } else {
        prompt += ' and subtle background ambiance';
      }
      prompt += '.';
    }

    return prompt.trim();
  }

  formatPrompt(prompt) {
    // Add visual formatting to the prompt
    return prompt
      .replace(/\. /g, '.<br><br>')
      .replace(/Audio includes/g, '<br><strong>Audio:</strong>')
      .replace(/Shot in/g, '<br><strong>Visual Style:</strong> Shot in');
  }

  getActorDescription(actorType) {
    const descriptions = {
      'customer_testimonial': 'authentic customer',
      'influencer_style': 'content creator',
      'expert_review': 'professional expert',
      'everyday_person': 'everyday individual'
    };
    return descriptions[actorType] || '';
  }

  getSettingDescription(setting) {
    const descriptions = {
      'home_bedroom': 'their cozy bedroom',
      'kitchen': 'a modern kitchen',
      'office': 'their workspace',
      'outdoors': 'a natural outdoor setting',
      'car': 'their car',
      'bathroom': 'their bathroom'
    };
    return descriptions[setting] || setting.replace('_', ' ');
  }

  getProductDemoDescription(demo) {
    const descriptions = {
      'hands-on': 'actively using',
      'results': 'showcasing results from',
      'unboxing': 'carefully unboxing'
    };
    return descriptions[demo] || 'demonstrating';
  }

  getCameraDescription(movement, composition) {
    let desc = '';
    if (composition) {
      const compositions = {
        'wide': 'Wide shot',
        'medium': 'Medium shot',
        'close-up': 'Close-up',
        'over-shoulder': 'Over-shoulder shot'
      };
      desc += compositions[composition] || composition;
    }

    if (movement) {
      const movements = {
        'static': 'with stable framing',
        'handheld': 'with natural handheld movement',
        'smooth_tracking': 'with smooth camera tracking',
        'close_zoom': 'zooming in dramatically'
      };
      desc += (desc ? ' ' : '') + (movements[movement] || movement);
    }

    return desc ? desc + '.' : '';
  }

  getLightingDescription(lighting) {
    const descriptions = {
      'natural': 'Natural lighting illuminates the scene.',
      'ring_light': 'Ring light provides even, flattering illumination.',
      'golden_hour': 'Golden hour lighting creates a warm, cinematic atmosphere.',
      'soft_indoor': 'Soft indoor lighting creates an intimate mood.'
    };
    return descriptions[lighting] || '';
  }

  getPerformanceDescription(style) {
    const descriptions = {
      'conversational': 'They speak in a conversational, natural tone:',
      'excited': 'With genuine excitement and enthusiasm, they exclaim:',
      'calm': 'In a calm, trustworthy manner, they explain:',
      'energetic': 'With high energy and passion, they share:',
      'emotional': 'With authentic emotion, they reveal:'
    };
    return descriptions[style] || 'They say:';
  }

  getHookTemplate(hookType, product) {
    const hook = appData.hook_types.find(h => h.id === hookType);
    if (!hook) return '';

    return hook.template
      .replace('[PRODUCT]', product)
      .replace('[TOPIC]', product)
      .replace('[PROBLEM]', 'this common issue')
      .replace('[AGITATION]', 'you ignore it')
      .replace('[NUMBER]', '1000+')
      .replace('[COMMON BELIEF]', 'this approach')
      .replace('[TIME/QUANTITY]', '24 hours');
  }

  updateQualityScore(data, prompt) {
    let totalScore = 0;
    const breakdown = document.getElementById('quality-breakdown');
    if (!breakdown) return;

    breakdown.innerHTML = '';

    appData.quality_metrics.forEach(metric => {
      let score = 0;

      switch(metric.element) {
        case 'Subject Description':
          if (data.ageRange || data.gender || data.actorType) score = metric.weight;
          break;
        case 'Action/Movement':
          if (data.productDemo || data.performanceStyle) score = metric.weight;
          break;
        case 'Setting/Context':
          if (data.setting) score = metric.weight;
          break;
        case 'Camera Work':
          if (data.cameraMovement || data.shotComposition) score = metric.weight;
          break;
        case 'Audio Elements':
          if (data.audioEnabled) score = metric.weight;
          break;
        case 'Sales Hook':
          if (data.hookType || data.customHook) score = metric.weight;
          break;
        case 'Product Integration':
          if (data.product && (data.productPlacement || data.productDemo)) score = metric.weight;
          break;
      }

      totalScore += score;

      const item = document.createElement('div');
      item.className = `quality-item ${score > 0 ? 'complete' : ''}`;
      item.textContent = metric.element;
      item.title = metric.description;
      breakdown.appendChild(item);
    });

    const scoreElement = document.getElementById('quality-score');
    if (scoreElement) {
      scoreElement.textContent = totalScore;
    }

    // Update score circle visual
    const circle = document.querySelector('.score-circle');
    if (circle) {
      const percentage = (totalScore / 100) * 360;
      circle.style.background = `conic-gradient(var(--color-primary) ${percentage}deg, var(--color-secondary) ${percentage}deg)`;
    }
  }

  updateWordCount(prompt) {
    const wordCount = prompt.split(/\s+/).filter(word => word.length > 0).length;
    const wordCountElement = document.getElementById('word-count');
    if (!wordCountElement) return;

    wordCountElement.textContent = wordCount;

    // Update styling based on word count
    const wordCountContainer = wordCountElement.parentElement;
    if (wordCountContainer) {
      wordCountContainer.className = 'word-count';

      if (wordCount >= 150 && wordCount <= 200) {
        wordCountContainer.classList.add('optimal');
      } else if (wordCount > 200) {
        wordCountContainer.classList.add('over');
      }
    }
  }

  copyPrompt() {
    const promptOutput = document.getElementById('prompt-output');
    if (!promptOutput) return;

    const promptText = promptOutput.textContent || promptOutput.innerText;

    if (!promptText || promptText.includes('Fill in the form')) {
      alert('Please generate a prompt first!');
      return;
    }

    const button = document.getElementById('copy-prompt');

    // Use modern clipboard API if available
    if (navigator.clipboard && window.isSecureContext) {
      navigator.clipboard.writeText(promptText).then(() => {
        this.showCopyFeedback(button);
      }).catch(() => {
        this.fallbackCopy(promptText, button);
      });
    } else {
      this.fallbackCopy(promptText, button);
    }
  }

  showCopyFeedback(button) {
    if (!button) return;

    const originalText = button.textContent;
    button.textContent = 'Copied!';
    button.classList.add('copied');

    setTimeout(() => {
      button.textContent = originalText;
      button.classList.remove('copied');
    }, 2000);
  }

  fallbackCopy(text, button) {
    try {
      const textarea = document.createElement('textarea');
      textarea.value = text;
      textarea.style.position = 'fixed';
      textarea.style.opacity = '0';
      document.body.appendChild(textarea);
      textarea.select();
      document.execCommand('copy');
      document.body.removeChild(textarea);

      this.showCopyFeedback(button);
    } catch (err) {
      alert('Unable to copy to clipboard. Please select and copy the text manually.');
    }
  }

  generateRandomSettings() {
    // Generate random selections for inspiration
    const randomSelections = {
      'ugc-type': this.getRandomOption(appData.ugc_types),
      'setting': this.getRandomOption(appData.settings),
      'lighting': this.getRandomOption(appData.lighting_types),
      'actor-type': this.getRandomOption(appData.actor_types),
      'hook-type': this.getRandomOption(appData.hook_types),
      'camera-movement': this.getRandomOption(appData.camera_movements),
      'age-range': ['18-25', '26-35', '36-45'][Math.floor(Math.random() * 3)],
      'gender': ['', 'female', 'male'][Math.floor(Math.random() * 3)],
      'performance-style': ['conversational', 'excited', 'calm', 'energetic'][Math.floor(Math.random() * 4)],
      'authenticity': ['raw', 'polished-amateur', 'semi-professional'][Math.floor(Math.random() * 3)],
      'shot-composition': ['wide', 'medium', 'close-up'][Math.floor(Math.random() * 3)],
      'visual-style': ['cinematic', 'documentary', 'social-native'][Math.floor(Math.random() * 3)]
    };

    // Apply random selections
    Object.entries(randomSelections).forEach(([fieldId, value]) => {
      const field = document.getElementById(fieldId);
      if (field && value) {
        field.value = value;
        if (fieldId === 'hook-type') {
          this.updateHookTemplate(value);
        }
      }
    });

    // Generate new prompt
    this.generatePrompt();

    // Add visual feedback
    const button = document.getElementById('generate-random');
    if (button) {
      button.style.transform = 'rotate(360deg)';
      setTimeout(() => {
        button.style.transform = '';
      }, 500);
    }
  }

  getRandomOption(array) {
    return array[Math.floor(Math.random() * array.length)].id;
  }

  loadTemplate(templateId) {
    if (!templateId) return;

    const template = appData.templates.find(t => t.id === templateId);
    if (!template) return;

    // Set form values based on template
    const templateMappings = {
      'product_review': {
        'ugc-type': 'review',
        'actor-type': 'customer_testimonial',
        'camera-movement': 'handheld',
        'lighting': 'natural',
        'performance-style': 'conversational',
        'authenticity': 'raw'
      },
      'unboxing_experience': {
        'ugc-type': 'unboxing',
        'product-demo': 'unboxing',
        'camera-movement': 'close_zoom',
        'lighting': 'natural',
        'performance-style': 'excited',
        'shot-composition': 'close-up'
      },
      'problem_solution': {
        'ugc-type': 'problem_solving',
        'hook-type': 'problem_agitate_solve',
        'performance-style': 'emotional',
        'camera-movement': 'static',
        'shot-composition': 'medium'
      }
    };

    const mapping = templateMappings[templateId];
    if (mapping) {
      Object.entries(mapping).forEach(([fieldId, value]) => {
        const field = document.getElementById(fieldId);
        if (field) {
          field.value = value;
        }
      });

      // Update hook template if needed
      if (mapping['hook-type']) {
        this.updateHookTemplate(mapping['hook-type']);
      }

      // Generate prompt with template settings
      this.generatePrompt();
    }

    // Reset template selector
    const templateSelector = document.getElementById('template-selector');
    if (templateSelector) {
      templateSelector.value = '';
    }
  }
}

// Initialize the application when DOM is loaded
document.addEventListener('DOMContentLoaded', () => {
  console.log('DOM loaded, initializing app...'); // Debug log
  new Veo3PromptGenerator();
});

