    margin: var(--space-2) 0 0;
    color: var(--text-secondary);
    font-size: 1.125rem;
}

.tutorial-main {
    max-width: 1400px;
    margin: 0 auto;
    padding: var(--space-8) var(--space-6);
    display: grid;
    grid-template-columns: 280px 1fr;
    gap: var(--space-8);
}

/* Tutorial Navigation */
.tutorial-nav {
    position: sticky;
    top: 120px;
    height: fit-content;
}

.nav-content {
    background: var(--bg-elevated);
    border: 1px solid var(--border-primary);
    border-radius: var(--radius-xl);
    padding: var(--space-6);
    box-shadow: var(--shadow-sm);
}

.nav-content h3 {
    margin: 0 0 var(--space-4);
    font-size: 1.125rem;
    font-weight: 600;
    color: var(--text-primary);
}

.nav-list {
    list-style: none;
    padding: 0;
    margin: 0;
}

.nav-list li {
    margin-bottom: var(--space-2);
}

.nav-link {
    display: block;
    padding: var(--space-3) var(--space-4);
    color: var(--text-secondary);
    text-decoration: none;
    border-radius: var(--radius-md);
    transition: all var(--transition-fast);
    font-weight: 500;
    border-left: 3px solid transparent;
}

.nav-link:hover {
    background: var(--bg-tertiary);
    color: var(--text-primary);
    border-left-color: var(--primary-400);
}

.nav-link.active {
    background: var(--primary-900);
    color: var(--primary-200);
    border-left-color: var(--primary-400);
}

/* Tutorial Content */
.tutorial-content {
    min-height: 100vh;
}

.tutorial-section {
    display: none;
    animation: fadeInUp 0.5s ease-out;
}

.tutorial-section.active {
    display: block;
}

.section-header {
    margin-bottom: var(--space-8);
    text-align: center;
}

.section-header h2 {
    margin: 0 0 var(--space-3);
    font-size: 2.5rem;
    font-weight: 700;
    color: var(--text-primary);
    display: flex;
    align-items: center;
    justify-content: center;
    gap: var(--space-3);
}

.section-header p {
    margin: 0;
    font-size: 1.25rem;
    color: var(--text-secondary);
    max-width: 600px;
    margin: 0 auto;
    line-height: 1.6;
}

/* Content Grid */
.content-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
    gap: var(--space-6);
    margin-bottom: var(--space-8);
}

.tutorial-card {
    background: var(--bg-elevated);
    border: 1px solid var(--border-primary);
    border-radius: var(--radius-xl);
    padding: var(--space-8);
    box-shadow: var(--shadow-sm);
    transition: all var(--transition-normal);
    position: relative;
    overflow: hidden;
}

.tutorial-card::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 4px;
    background: var(--gradient-accent);
    transform: scaleX(0);
    transition: transform var(--transition-normal);
}

.tutorial-card:hover {
    box-shadow: var(--shadow-xl);
    transform: translateY(-4px);
}

.tutorial-card:hover::before {
    transform: scaleX(1);
}

.tutorial-card.full-width {
    grid-column: 1 / -1;
}

.card-icon {
    width: 60px;
    height: 60px;
    background: var(--gradient-primary);
    border-radius: var(--radius-xl);
    display: flex;
    align-items: center;
    justify-content: center;
    margin-bottom: var(--space-4);
}

.card-icon i {
    font-size: 1.75rem;
    color: white;
}

.tutorial-card h3 {
    margin: 0 0 var(--space-4);
    font-size: 1.5rem;
    font-weight: 600;
    color: var(--text-primary);
}

.tutorial-card p {
    margin: 0 0 var(--space-4);
    color: var(--text-secondary);
    line-height: 1.6;
}

/* Special Content Boxes */
.highlight-box,
.tip-box,
.warning-box,
.example-box {
    padding: var(--space-4);
    border-radius: var(--radius-lg);
    margin: var(--space-4) 0;
    border-left: 4px solid;
}

.highlight-box {
    background: var(--primary-900);
    border-left-color: var(--primary-400);
    color: var(--primary-200);
}

.tip-box {
    background: var(--success-900);
    border-left-color: var(--success-400);
    color: var(--success-200);
}

.warning-box {
    background: var(--warning-900);
    border-left-color: var(--warning-400);
    color: var(--warning-200);
}

.example-box {
    background: var(--bg-tertiary);
    border-left-color: var(--border-secondary);
    color: var(--text-secondary);
}

.tip-box i,
.warning-box i {
    margin-right: var(--space-2);
    font-size: 1.125rem;
}

/* Process Steps */
.process-steps {
    display: flex;
    flex-direction: column;
    gap: var(--space-4);
}

.step {
    display: flex;
    align-items: flex-start;
    gap: var(--space-4);
    padding: var(--space-4);
    background: var(--bg-secondary);
    border-radius: var(--radius-lg);
    border: 1px solid var(--border-primary);
}

.step-number {
    width: 32px;
    height: 32px;
    background: var(--gradient-primary);
    color: white;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-weight: 600;
    flex-shrink: 0;
}

.step-content strong {
    display: block;
    color: var(--text-primary);
    margin-bottom: var(--space-1);
}

.step-content p {
    margin: 0;
    color: var(--text-secondary);
    font-size: 0.9rem;
}

/* Quick Start List */
.quick-start-list {
    display: flex;
    flex-direction: column;
    gap: var(--space-3);
    margin: var(--space-4) 0;
}

.quick-step {
    display: flex;
    align-items: center;
    gap: var(--space-3);
    padding: var(--space-3);
    background: var(--bg-secondary);
    border-radius: var(--radius-md);
    border: 1px solid var(--border-primary);
}

.quick-step i {
    color: var(--success-400);
    font-size: 1.25rem;
    flex-shrink: 0;
}

/* Method Details */
.method-details h4,
.testing-guide h4,
.optimization-tips h4 {
    margin: var(--space-4) 0 var(--space-2);
    color: var(--text-primary);
    font-size: 1.125rem;
}

.method-details ul,
.testing-guide ul,
.optimization-tips ul {
    margin: 0 0 var(--space-4);
    padding-left: var(--space-5);
}

.method-details li,
.testing-guide li,
.optimization-tips li {
    margin-bottom: var(--space-2);
    color: var(--text-secondary);
    line-height: 1.5;
}

.avoid-list {
    list-style: none;
    padding-left: 0;
}

.avoid-list li {
    position: relative;
    padding-left: var(--space-5);
    color: var(--error-300);
}

.avoid-list li::before {
    content: '';
    position: absolute;
    left: 0;
    color: var(--error-400);
    font-weight: bold;
}

/* Content Types */
.content-types {
    display: flex;
    flex-direction: column;
    gap: var(--space-3);
}

.type-item {
    display: flex;
    align-items: flex-start;
    gap: var(--space-3);
    padding: var(--space-3);
    background: var(--bg-secondary);
    border-radius: var(--radius-md);
    border: 1px solid var(--border-primary);
}

.type-item i {
    color: var(--primary-400);
    font-size: 1.5rem;
    flex-shrink: 0;
    margin-top: var(--space-1);
}

.type-item strong {
    display: block;
    color: var(--text-primary);
    margin-bottom: var(--space-1);
}

.type-item p {
    margin: 0;
    color: var(--text-secondary);
    font-size: 0.9rem;
}

/* Actors Grid */
.actors-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: var(--space-4);
    margin: var(--space-4) 0;
}

.actor-profile {
    padding: var(--space-4);
    background: var(--bg-secondary);
    border-radius: var(--radius-lg);
    border: 1px solid var(--border-primary);
}

.actor-info strong {
    display: block;
    color: var(--text-primary);
    margin-bottom: var(--space-2);
    font-size: 1.1rem;
}

.actor-info p {
    margin: var(--space-1) 0;
    color: var(--text-secondary);
    font-size: 0.9rem;
}

/* Footer */
.tutorial-footer {
    background: var(--bg-secondary);
    border-top: 1px solid var(--border-primary);
    padding: var(--space-8) 0;
    margin-top: var(--space-12);
}

.footer-content {
    max-width: 1400px;
    margin: 0 auto;
    padding: 0 var(--space-6);
    display: flex;
    align-items: center;
    justify-content: space-between;
}

.footer-links {
    display: flex;
    gap: var(--space-6);
}

.footer-links a {
    color: var(--text-secondary);
    text-decoration: none;
    font-weight: 500;
    transition: color var(--transition-fast);
}

.footer-links a:hover {
    color: var(--primary-400);
}

/* Responsive Design */
@media (max-width: 1024px) {
    .tutorial-main {
        grid-template-columns: 1fr;
        gap: var(--space-6);
    }
    
    .tutorial-nav {
        position: static;
        order: -1;
    }
    
    .nav-content {
        padding: var(--space-4);
    }
    
    .nav-list {
        display: flex;
        flex-wrap: wrap;
        gap: var(--space-2);
    }
    
    .nav-list li {
        margin-bottom: 0;
    }
    
    .nav-link {
        padding: var(--space-2) var(--space-3);
        border-left: none;
        border-bottom: 3px solid transparent;
    }
    
    .nav-link:hover,
    .nav-link.active {
        border-left: none;
        border-bottom-color: var(--primary-400);
    }
}

@media (max-width: 768px) {
    .tutorial-header .header-content {
        flex-direction: column;
        gap: var(--space-4);
        text-align: center;
    }
    
    .tutorial-main {
        padding: var(--space-6) var(--space-4);
    }
    
    .content-grid {
        grid-template-columns: 1fr;
        gap: var(--space-4);
    }
    
    .section-header h2 {
        font-size: 2rem;
        flex-direction: column;
        gap: var(--space-2);
    }
    
    .tutorial-card {
        padding: var(--space-6);
    }
    
    .footer-content {
        flex-direction: column;
        gap: var(--space-4);
        text-align: center;
    }
}

/* Animations */
@keyframes fadeInUp {
    from {
        opacity: 0;
        transform: translateY(30px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

/* Smooth scrolling */
html {
    scroll-behavior: smooth;
}

/* Focus styles for accessibility */
.nav-link:focus,
.tutorial-card:focus {
    outline: 2px solid var(--primary-400);
    outline-offset: 2px;
}

